import { CombinedState, combineReducers } from "redux"
const initialstate = {
    cart : []
}


export const userreducer = (state = initialstate,action) =>{
    switch(action.type){
        case 'Add_cart':
            return {
                ...state,
                user:[...state.cart,action.payload]
            }

        case 'Login_failure' :
            return{
                ...state,
            }
        default :
        return state;
    }
}

const rootreducer = combineReducers({userreducer})

export default rootreducer;
