import { CombinedState, combineReducers } from "redux"
const initialstate = {
    cart : [],
    user:[]
}



export const userreducer = (state = initialstate,action) =>{
    switch(action.type){
        case 'Add_cart':
            return {
                ...state,
                cart:[...state.cart,action.payload]
            }

        case 'Failure' :
            return{
                ...state,
            }
        default :
        return state;
    }
}

export const loginreducer = (state = initialstate,action)=>{
    switch(action.typle){
        case 'Login_success':
            return{
                ...state.user,
                user:[...state.user,action.payload]
            }
        case "login_failure":
            return{
                ...state.user,
            }
        default:
            return state.user
    }
}

const rootreducer = combineReducers({cart: userreducer})

export default rootreducer;
