  // })
  // useEffect(()=>{
  //   user_data();
  // })
  // console.log('user_id', user_id)

  
  const navigate = useNavigate();
  
  const price = props.price
  console.log(price)

const data_id = useSelector((state) => state.user.user)

  const data_data = ()=>{
    const user_data1 = data_id.map((userID)=>{
      const {id} = userID
      setUserId(id)
      console.log("id", id)
    })
  }

  useEffect(()=>{
    data_data();
  })

  
  

  // useEffect(()=>{
  //   if(data1){
  //     setUserId(data1.id)
  //   } else{
  //     setUserId('')
  //   }

  // },[data1])
  
  const addToCart = async()=>{
    
    const response = await axios.post(`http://127.0.0.1:8000/add/${props.id}/`,{
      user_id,price
    })
    console.log(response.data)
    
    
  }
  const add_cart = (e)=>{
    dispatch(Add(e))
  }

 
  
  
 

  

  return (
    <div className='products' key={props.id}>
    <Card key={props.id}   style={{ width: "inherit" }}>
        <Card.Img variant="top" src={props.image}  key = {props.id}  />
        <Card.Body>
          <Card.Title>Card Title</Card.Title>
          <Card.Text>
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </Card.Text>
          <div className="product-actions">
            <p>{props.price}</p>
            <Button onClick={() => addToCart(navigate('/cart'))}  variant="primary">Buy Now</Button>
            <Button onClick={addToCart} variant="secondary">Add to cart</Button>
            <Button onClick={() => add_cart(props.product)} variant="secondary">Add to cart</Button>
          </div>
        </Card.Body>
      </Card>
      
    </div>
  )
}
