import React from 'react'
import { useState, useEffect } from 'react';
import axios from 'axios';
import 'react-multi-carousel/lib/styles.css';
import Slider from "react-slick";
import { Card, Button } from "react-bootstrap";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function Product(props) {
    const [product, setProduct] = useState([]);
    const [slide] = useState(
        {
            img: "https://dummyimage.com/600x400/000/7CFC00"
          },
          {
            img: "https://dummyimage.com/600x400/000/ccccc"
          },
          {
            img: "https://dummyimage.com/600x400/000/dddddd"
          },
          {
            img: "https://dummyimage.com/600x400/000/fff"
          },
          {
            img: "https://dummyimage.com/600x400/000/B22222"
          },
          {
            img: "https://dummyimage.com/600x400/000/7CFC00"
          },
          {
            img: "https://dummyimage.com/600x400/000/ccccc"
          },
          {
            img: "https://dummyimage.com/600x400/000/dddddd"
          },
          {
            img: "https://dummyimage.com/600x400/000/B22222"
          },
          {
            img: "https://dummyimage.com/600x400/000/7CFC00"
          }
    )
    const fetchproducts = async () => {
        const response = await axios.get('http://127.0.0.1:8000/product/')
        try {
            setProduct(response.data)
            console.log(response.data)
            console.log(product)
        } catch (error) {
            console.log('error' + error)

        }
    }
    useEffect(() => {
        fetchproducts();
        // eslint-disable-next-line
    }, [])


    var settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 4,
        initialSlide: 0,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2
                }
            }
        ]
    };
    let base_url = ''
    return (
        <div>
        <h2> Responsive Product Carousel</h2>
        <Slider {...settings}>
          {slide.map((slide, index) => (
             
              <div key={index}>
                <p>{product.title}</p>
                <img src={slide.img} alt={`slide${index}`} />
              </div>
          
          ))}
        </Slider>
      </div>
    )
}
