import React from 'react'
import { Card } from 'react-bootstrap'
import Header from './header'
import './index.css'
import { Link } from 'react-router-dom'
import axios from 'axios'
import { useState,useEffect } from 'react'

export default function Address() {
    const id = JSON.parse(localStorage.getItem('data'))
    const user_id = id.id
    const [address,setAddress] = useState([])


    const getAddress = async()=>{
        const response = await axios.get('http://127.0.0.1:8000/address/',user_id)
        try {
            setAddress(response.data)
            console.log(response.data)
        } catch (error) {
            console.log('error' , error);
        }
    }

    useEffect(()=>{
        getAddress();
        //eslint-disable-next-line
    },[])
  return (
      <>
    <Header/>


    <div className='user_dashboard'>
    <Card style={{ width: "inherit" }} id = "user_card">
       
        <Card.Body>
          <Card.Title className='card_title'>DASHBOARD</Card.Title>
          <Card.Text>
          <p className='user_profile'>My profile</p>
           <span> <i className='line'></i></span>
           <p className='user_orders'><a href="#/">My orders</a></p>
           <span> <i className='line'></i></span>
           
           <p className='user_address'><Link to='/address'>Address</Link></p>
           <span> <i className='line'></i></span>
          </Card.Text>
          <div className="product-actions">
           
          
          
          </div>
        </Card.Body>
      </Card>
      
    </div>
    <div className='user_address'>
        {address.map((address,index)=>(

        
      <Card style={{ width: '18rem' }} id = "address_card">
      <Card.Img variant="top" src="holder.js/100px180" />
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
      </Card.Body>
    </Card>
    ))}
    </div>
    </>
  )
}
